import "./styles.css";
import Logo from "../../assets/logo.png";
import Search from "../../assets/search.png";
import Pokeball from "../../assets/pokeball.png";

import axios from "axios";
import { useState, useEffect } from "react";
import Pokemon from "../../components/Pokemon";

const Home = () => {
  const [pokemons, setPokemons] = useState([]);
  const [type, setType] = useState("");

  useEffect(() => {
    axios
      .get(
        "https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json"
      )
      .then(function (response) {
        setPokemons(response.data.pokemon);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const displayPokemons = () => {
    if (type === "") {
      return pokemons.map((pokemon) => (
        <Pokemon
          img={pokemon.img}
          name={pokemon.name}
          type={pokemon.type}
          height={pokemon.height}
          weight={pokemon.weight}
        />
      ));
    } else {
      for (let selectpokemon of pokemons) {
        if (type === selectpokemon.type[0]) {
          return (
            <Pokemon
              img={selectpokemon.img}
              name={selectpokemon.name}
              type={selectpokemon.type}
              height={selectpokemon.height}
              weight={selectpokemon.weight}
            />
          );
        }
      }
    }
  };

  // useEffect(() => {
  //   displayPokemons();
  // }, [type]);

  const typeSelect = (type) => {
    setType(type);
    console.log(type);
  };

  return (
    <div className="home">
      <header>
        <img src={Logo} alt="Pokemon logo" className="logo" />
        <h1>Uma Pokedex completa criada em React JS</h1>
        <input type="text" placeholder="Search a Pokemon" />
        <button>
          <img src={Search} alt="Magnifying glass" />
        </button>
      </header>
      <div className="filter">
        <div className="filter-title">
          <img src={Pokeball} alt="Pokeball" />
          <h2>Filter by type</h2>
        </div>
        <hr />
        <div className="type-list">
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Normal"
          >
            NORMAL
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Fighting"
          >
            FIGHTING
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Flying"
          >
            FLYING
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Poison"
          >
            POISON
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Ground"
          >
            GROUND
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Rock"
          >
            ROCK
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Bug"
          >
            BUG
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Ghost"
          >
            GHOST
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Steel"
          >
            STEEL
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Fire"
          >
            FIRE
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Water"
          >
            WATER
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Grass"
          >
            GRASS
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Eletric"
          >
            ELETRIC
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Psychic"
          >
            PSYCHIC
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Ice"
          >
            ICE
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Dragon"
          >
            DRAGON
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Dark"
          >
            DARK
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Fairy"
          >
            FAIRY
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Shadow"
          >
            SHADOW
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Unknown"
          >
            UNKNOWN
          </button>
        </div>
      </div>
      <hr />
      <div className="pokemon-list">{displayPokemons()}</div>
    </div>
  );
};

export default Home;
