import "./styles.css";

const Modal = ({ pokemon, selected }) => {
  return pokemon && selected ? (
    <div
      className="modal"
      onClick={(e) => {
        if (e.style.display === "none") {
          e.target.display = "absolute";
        }
        e.target.style.display = "none";
      }}
    >
      <div className="card">
        <div className="image-container">
          <img src={pokemon.img} alt="Pokemon" />
        </div>
        <h3>{pokemon.name}</h3>
        <h5>{pokemon.height}</h5>
        <h5>{pokemon.weight}</h5>
      </div>
      <h5>
        {pokemon.type.map((t, index) => {
          return `${t}${index !== pokemon.type.length - 1 ? ", " : ""}`;
        })}
      </h5>
      <h5>{pokemon.candy}</h5>
      <h5>{pokemon.candy_count}</h5>
      <h5>{pokemon.egg}</h5>
      <h5>{pokemon.spawn_chance}</h5>
      <h5>{pokemon.avg_spawns}</h5>
      <h5>{pokemon.spawn_time}</h5>
      <h5>
        {pokemon.multipliers?.map((t, index) => {
          return `${t}${index !== pokemon.multipliers.length - 1 ? ", " : ""}`;
        })}
      </h5>
      <h5>
        {pokemon.weaknesses.map((t, index) => {
          return `${t}${index !== pokemon.weaknesses.length - 1 ? ", " : ""}`;
        })}
      </h5>
    </div>
  ) : (
    ""
  );
};

export default Modal;
