import "./styles.css";
import Logo from "../../assets/logo.png";
import Search from "../../assets/search.png";
import Pokeball from "../../assets/pokeball.png";

import axios from "axios";
import { useState, useEffect } from "react";
import Pokemon from "../../components/Pokemon";

const Home = () => {
  const [pokemons, setPokemons] = useState([]);
  const [type, setType] = useState("");

  useEffect(() => {
    axios
      .get(
        "https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json"
      )
      .then(function (response) {
        setPokemons(response.data.pokemon);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const displayPokemons = (type) => {
    if (type === "") {
      return pokemons.map((pokemon) => (
        <Pokemon
          img={pokemon.img}
          name={pokemon.name}
          type={pokemon.type}
          height={pokemon.height}
          weight={pokemon.weight}
        />
      ));
    }
  };

  const typeSelect = (type) => {
    switch (type) {
      case "NORMAL":
        displayPokemons(type);
        break;

      case "Fighting":
        color = { backgroundColor: "#b64d1c" };
        break;

      case "Flying":
        color = { backgroundColor: "#277ba1" };
        break;

      case "Poison":
        color = { backgroundColor: "#8456c3" };
        break;

      case "Ground":
        color = { backgroundColor: "#8a8331" };
        break;

      case "Rock":
        color = { backgroundColor: "#715c3d" };
        break;

      case "Bug":
        color = { backgroundColor: "#419651" };
        break;

      case "Ghost":
        color = { backgroundColor: "#83417a" };
        break;

      case "Steel":
        color = { backgroundColor: "#6c6c6c" };
        break;

      case "Fire":
        color = { backgroundColor: "#a9302b" };
        break;

      case "Water":
        color = { backgroundColor: "#135eab" };
        break;

      case "Grass":
        color = { backgroundColor: "#7d8545" };
        break;

      case "Eletric":
        color = { backgroundColor: "#ba9a22" };
        break;

      case "Psychic":
        color = { backgroundColor: "#473a8d" };
        break;

      case "Ice":
        color = { backgroundColor: "#659dba" };
        break;

      case "Dragon":
        color = { backgroundColor: "#d57931" };
        break;

      case "Dark":
        color = { backgroundColor: "#232424" };
        break;

      case "Fairy":
        color = { backgroundColor: "#ba65a0" };
        break;

      case "Shadow":
        color = { backgroundColor: "#414141" };
        break;

      case "Unknown":
        color = { backgroundColor: "#9a9a9a" };
        break;

      default:
        return "#419651";
    }
  };

  return (
    <div className="home">
      <header>
        <img src={Logo} alt="Pokemon logo" className="logo" />
        <h1>Uma Pokedex completa criada em React JS</h1>
        <input type="text" placeholder="Search a Pokemon" />
        <button
          onClick={() => {
            pokemons.map((pokemon) => {
              return console.log(pokemon.name);
            });
          }}
        >
          <img src={Search} alt="Magnifying glass" />
        </button>
      </header>
      <div className="filter">
        <div className="filter-title">
          <img src={Pokeball} alt="Pokeball" />
          <h2>Filter by type</h2>
        </div>
        <hr />
        <div className="type-list">
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="NORMAL"
          >
            NORMAL
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="FIGHTING"
          >
            FIGHTING
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="FLYING"
          >
            FLYING
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="POISON"
          >
            POISON
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="GROUND"
          >
            GROUND
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="ROCK"
          >
            ROCK
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="BUG"
          >
            BUG
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="GHOST"
          >
            GHOST
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="STEEL"
          >
            STEEL
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="FIRE"
          >
            FIRE
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="WATER"
          >
            WATER
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="GRASS"
          >
            GRASS
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="ELETRIC"
          >
            ELETRIC
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="PSYCHIC"
          >
            PSYCHIC
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="ICE"
          >
            ICE
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="DRAGON"
          >
            DRAGON
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="DARK"
          >
            DARK
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="FAIRY"
          >
            FAIRY
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="SHADOW"
          >
            SHADOW
          </button>
          <button
            onClick={(e) => typeSelect(e.target.innerHTML)}
            className="type-button"
            id="UNKNOWN"
          >
            UNKNOWN
          </button>
        </div>
      </div>
      <hr />
      <div className="pokemon-list">{displayPokemons(type)}</div>
    </div>
  );
};

export default Home;
