import "./styles.css";

const Pokemon = ({ img, name, type, height, weight, onClick }) => {
  let color = { backgroundColor: "#419651" };
  let fontColor = { color: "white" };

  const handleColor = () => {
    switch (type[0]) {
      case "Normal":
        return { backgroundColor: "white" };

      case "Fighting":
        return { backgroundColor: "#b64d1c" };

      case "Flying":
        return { backgroundColor: "#277ba1" };

      case "Poison":
        return { backgroundColor: "#8456c3" };

      case "Ground":
        return { backgroundColor: "#8a8331" };

      case "Rock":
        return { backgroundColor: "#715c3d" };

      case "Bug":
        return { backgroundColor: "#419651" };

      case "Ghost":
        return { backgroundColor: "#83417a" };

      case "Steel":
        return { backgroundColor: "#6c6c6c" };

      case "Fire":
        return { backgroundColor: "#a9302b" };

      case "Water":
        return { backgroundColor: "#135eab" };

      case "Grass":
        return { backgroundColor: "#7d8545" };

      case "Eletric":
        return { backgroundColor: "#ba9a22" };

      case "Psychic":
        return { backgroundColor: "#473a8d" };

      case "Ice":
        return { backgroundColor: "#659dba" };

      case "Dragon":
        return { backgroundColor: "#d57931" };

      case "Dark":
        return { backgroundColor: "#232424" };

      case "Fairy":
        return { backgroundColor: "#ba65a0" };

      case "Shadow":
        return { backgroundColor: "#414141" };

      case "Unknown":
        return { backgroundColor: "#9a9a9a" };

      default:
        return "#419651";
    }
  };

  return (
    <div style={handleColor()} className="card" onClick={onClick}>
      <div className="image-container">
        <img src={img} alt="Pokemon" />
      </div>
      <h3 style={fontColor} className="name">
        {name}
      </h3>
      <h5 style={fontColor} className="type">
        {type.toString()}
      </h5>
      <h5 style={fontColor} className="height">
        {height}
      </h5>
      <h5 style={fontColor} className="weight">
        {weight}
      </h5>
    </div>
  );
};

export default Pokemon;
