import "./styles.css";

const Modal = ({ pokemon, setPokemon }) => {
  let color = { backgroundColor: "#419651" };

  const handleColor = () => {
    pokemon.type.map((t) => {
      switch (t) {
        case "Normal":
          return (color = { backgroundColor: "white" });

        case "Fighting":
          return (color = { backgroundColor: "#b64d1c" });
          break;

        case "Flying":
          return (color = { backgroundColor: "#277ba1" });
          break;

        case "Poison":
          return (color = { backgroundColor: "#8456c3" });
          break;

        case "Ground":
          return (color = { backgroundColor: "#8a8331" });
          break;

        case "Rock":
          return (color = { backgroundColor: "#715c3d" });
          break;

        case "Bug":
          return (color = { backgroundColor: "#419651" });
          break;

        case "Ghost":
          return (color = { backgroundColor: "#83417a" });
          break;

        case "Steel":
          return (color = { backgroundColor: "#6c6c6c" });
          break;

        case "Fire":
          return (color = { backgroundColor: "#a9302b" });
          break;

        case "Water":
          return (color = { backgroundColor: "#135eab" });
          break;

        case "Grass":
          return (color = { backgroundColor: "#7d8545" });
          break;

        case "Eletric":
          return (color = { backgroundColor: "#ba9a22" });
          break;

        case "Psychic":
          return (color = { backgroundColor: "#473a8d" });
          break;

        case "Ice":
          return (color = { backgroundColor: "#659dba" });
          break;

        case "Dragon":
          return (color = { backgroundColor: "#d57931" });

        case "Dark":
          return (color = { backgroundColor: "#232424" });

        case "Fairy":
          return (color = { backgroundColor: "#ba65a0" });

        case "Shadow":
          return (color = { backgroundColor: "#414141" });

        case "Unknown":
          return (color = { backgroundColor: "#9a9a9a" });

        default:
          return "#419651";
      }
    });
  };

  handleColor();

  return (
    pokemon && (
      <div className="modal-container" onClick={() => setPokemon("")}>
        <div className="modal" onClick={(e) => e.stopPropagation()}>
          <div className="card-modal">
            <div className="image-container">
              <img src={pokemon.img} alt="Pokemon" />
            </div>
            <h3>{pokemon.name}</h3>
            <h5>{pokemon.height}</h5>
            <h5>{pokemon.weight}</h5>
          </div>
          <div className="card-modal card-info">
            <div className="atributes candy">
              <b>Candy</b>
              <h5>{pokemon.candy}</h5>
              <h5 className="subtitle">Quantity</h5>
              <h5>{pokemon.candy_count ? pokemon.candy_count : "No Candy"}</h5>
            </div>
            <div className="atributes egg">
              <b>Egg</b>
              <h5>{pokemon.egg}</h5>
            </div>
            <div className="atributes spawn">
              <b>Spawn</b>
              <h5 className="subtitle">Spawn Chance </h5>
              <h5>{pokemon.spawn_chance}</h5>
              <h5 className="subtitle">Average Spawns </h5>
              <h5>{pokemon.avg_spawns}</h5>
              <h5 className="subtitle">Spawn Time </h5>
              <h5>{pokemon.spawn_time}</h5>
            </div>
            <div className="atributes multipliers">
              <b>Multipliers</b>
              <h5>
                {pokemon.multipliers?.map((t, index) => {
                  return `${t}${
                    index !== pokemon.multipliers.length - 1 ? ", " : ""
                  }`;
                })
                  ? pokemon.multipliers?.map((t, index) => {
                      return `${t}${
                        index !== pokemon.multipliers.length - 1 ? ", " : ""
                      }`;
                    })
                  : "No multipliers"}
              </h5>
            </div>
            <div className="atributes weaknessess">
              <b>Weaknesses</b>
              <h5>
                {pokemon.weaknesses?.map((t, index) => {
                  return `${t}${
                    index !== pokemon.weaknesses.length - 1 ? ", " : ""
                  }`;
                })}
              </h5>
            </div>
          </div>
          <div className="card-modal card-type">
            <div className="atributes type">
              <b>Type</b>
              <div className="type-card">
                {pokemon.type.map((t) => {
                  return (
                    <h5 className="types" style={color}>
                      {t}
                    </h5>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  );
};

export default Modal;
