import "./styles.css";
import Logo from "../../assets/logo.png";
import Search from "../../assets/search.png";
import Pokeball from "../../assets/pokeball.png";
import NotFound from "../../assets/not-found.png";

import axios from "axios";
import { useState, useEffect, useMemo } from "react";
import Pokemon from "../../components/Pokemon";

const Home = () => {
  const [pokemons, setPokemons] = useState([]);
  const [type, setType] = useState("");
  const [search, setSearch] = useState("");
  // const [selectPokemon, setSelectedPokemon] = useState("");

  useEffect(() => {
    axios
      .get(
        "https://raw.githubusercontent.com/Biuni/PokemonGO-Pokedex/master/pokedex.json"
      )
      .then(function (response) {
        setPokemons(response.data.pokemon);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const displayPokemons = useMemo(() => {
    return pokemons
      .filter((pokemon) => {
        if (pokemon.type.includes(type) || !type) {
          return true;
        }
        return false;
      })
      .filter((pokemon) => {
        if (
          pokemon.name.toLowerCase().includes(search.toLowerCase()) ||
          !search
        ) {
          return true;
        }
        return false;
      })
      .map((pokemon) => (
        <Pokemon
          onClick={console.log(pokemon.name)}
          key={pokemon.id}
          img={pokemon.img}
          name={pokemon.name}
          type={pokemon.type}
          height={pokemon.height}
          weight={pokemon.weight}
        />
      ));
  }, [type, pokemons, search]);

  const typeSelect = (type) => {
    setType(type);
  };

  return (
    <div className="home">
      <header>
        <img src={Logo} alt="Pokemon logo" className="logo" />
        <h1>Uma Pokedex completa criada em React JS</h1>
        <input
          type="text"
          placeholder="Search a Pokemon"
          onChange={(e) => setSearch(e.target.value)}
        />
        <button>
          <img src={Search} alt="Magnifying glass" />
        </button>
      </header>
      <div className="filter">
        <div className="filter-title">
          <img src={Pokeball} alt="Pokeball" />
          <h2>Filter by type</h2>
        </div>
        <hr />
        <div className="type-list">
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Normal"
          >
            NORMAL
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Fighting"
          >
            FIGHTING
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Flying"
          >
            FLYING
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Poison"
          >
            POISON
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Ground"
          >
            GROUND
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Rock"
          >
            ROCK
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Bug"
          >
            BUG
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Ghost"
          >
            GHOST
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Steel"
          >
            STEEL
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Fire"
          >
            FIRE
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Water"
          >
            WATER
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Grass"
          >
            GRASS
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Eletric"
          >
            ELETRIC
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Psychic"
          >
            PSYCHIC
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Ice"
          >
            ICE
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Dragon"
          >
            DRAGON
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Dark"
          >
            DARK
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Fairy"
          >
            FAIRY
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Shadow"
          >
            SHADOW
          </button>
          <button
            onClick={(e) => typeSelect(e.target.id)}
            className="type-button"
            id="Unknown"
          >
            UNKNOWN
          </button>
        </div>
      </div>
      <hr />
      <div className="pokemon-list">
        {displayPokemons.length ? (
          displayPokemons
        ) : (
          <div className="no-pokemons">
            <img className="not-found" src={NotFound} alt="Pokemon Not Found" />{" "}
            <h2>404 Pokemon not Found</h2>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
