import "./styles.css";
import Pokemon from "../Pokemon";

const Modal = ({ pokemon, selected }) => {
  return (
    <div className="modal">
      <div className="image-container">
        <img src={pokemon.img} alt="Pokemon" />
      </div>
      <div className="card">
        <h3>{pokemon.name}</h3>
        <h5>{pokemon.height}</h5>
        <h5>{pokemon.weight}</h5>
        <h5>
          {pokemon.type.map((t, index) => {
            return `${t}${index !== pokemon.type.lenght ? ", " : ""}`;
          })}
        </h5>
      </div>
    </div>
  );
};

export default Modal;
