import "./styles.css";

const Pokemon = ({ img, name, type, height, weight }) => {
  let color = { backgroundColor: "#419651" };

  const handleColor = () => {
    switch (type[0]) {
      case "Normal":
        color = { backgroundColor: "white" };
        break;

      case "Fighting":
        color = { backgroundColor: "#b64d1c" };
        break;

      case "Flying":
        color = { backgroundColor: "#277ba1" };
        break;

      case "Poison":
        color = { backgroundColor: "#8456c3" };
        break;

      case "Ground":
        color = { backgroundColor: "#8a8331" };
        break;

      case "Rock":
        color = { backgroundColor: "#715c3d" };
        break;

      case "Bug":
        color = { backgroundColor: "#419651" };
        break;

      case "Ghost":
        color = { backgroundColor: "#83417a" };
        break;

      case "Steel":
        color = { backgroundColor: "#6c6c6c" };
        break;

      case "Fire":
        color = { backgroundColor: "#a9302b" };
        break;

      case "Fire":
        color = { backgroundColor: "#a9302b" };
        break;

      default:
        return "#419651";
    }
  };

  handleColor();

  return (
    <div style={color} className="card">
      <div className="image-container">
        <img src={img} alt="Pokemon" />
      </div>
      <h2 className="name">{name}</h2>
      <h3 className="type">{type[0] + ", " + type[1]}</h3>
      <h3 className="height">{height}</h3>
      <h3 className="weight">{weight}</h3>
    </div>
  );
};

export default Pokemon;
